const orderLink=document.querySelectorAll(".order-button"),orderPopup=document.querySelector(".modal-order"),orderClose=orderPopup.querySelector(".modal-close"),orderContinue=orderPopup.querySelector(".modal-order-continue"),orderShow=e=>{e.preventDefault(),orderPopup.classList.add("modal-show")},orderHide=e=>{e.preventDefault(),orderPopup.classList.remove("modal-show")};window.addEventListener("keydown",function(e){27===e.keyCode&&orderPopup.classList.contains("modal-show")&&(e.preventDefault(),orderPopup.classList.remove("modal-show"),orderPopup.classList.remove("modal-error"))}),orderLink.forEach(e=>{e.addEventListener("click",orderShow)}),orderClose.addEventListener("click",orderHide),orderContinue.addEventListener("click",orderHide);